mapper.select:
SELECT gz.id, gz.turnover, gz.gp_code, gz.number as number, DATE_FORMAT(gz.created_date,'%y-%m-%d' ) as date, gz.current_price FROM gp_zx gz WHERE gz.id IN ( select max( gc.id ) FROM gp_zx gc
and gc.gp_code=#{gpCode}
GROUP BY DATE_FORMAT( gc.created_date, '%Y-%m-%d' ) )
and gz.created_date BETWEEN #{startDate} and #{endDate}

SELECT gz.id, gz.turnover, gz.gp_code, gz.number as number, DATE_FORMAT(gz.created_date,'%y-%m-%d' ) as date, gz.current_price FROM gp_yl gz WHERE gz.id IN ( select max( gz.id ) FROM gp_yl gz
and gz.gp_code=#{gpCode}
GROUP BY DATE_FORMAT( gz.created_date, '%Y-%m-%d' ) )
and gz.created_date BETWEEN #{startDate} and #{endDate}

SELECT gz.id, gz.turnover, gz.gp_code, gz.number/10000 as number, DATE_FORMAT(gz.created_date,'%y-%m-%d' ) as date, gz.current_price FROM gp_yl gz WHERE gz.id IN ( select max( gz.id ) FROM gp_yl gz
and gz.gp_code=#{gpCode}
GROUP BY DATE_FORMAT( gz.created_date, '%Y-%m-%d' ) )

SELECT gz.id, gz.turnover, gz.gp_code, gz.number as number, DATE_FORMAT(gz.created_date,'%H:%i:%s') as date, gz.current_price FROM gp_zx gz
and gz.gp_code=#{gpCode}
and gz.created_date BETWEEN #{startDate} and #{endDate}
order by gz.id asc

SELECT gz.id, gz.turnover, gz.gp_code, gz.number as number, DATE_FORMAT(gz.created_date,'%H:%i:%s') as date, gz.current_price FROM gp_yl gz
and gz.gp_code=#{gpCode}
and gz.created_date BETWEEN #{startDate} and #{endDate}
order by gz.id asc
