mapper.select:
select
       id,
       question,
       solution,
       ask_num,
       tenant_id,
       create_by,
       create_time,
       update_by,
       update_time,
       version_num,
 from  ics_question

     1=1

   and id=#{id}


     and question=#{question}


     and solution=#{solution}


   and ask_num=#{askNum}


   and tenant_id=#{tenantId}


   and create_by=#{createBy}


   and create_time=#{createTime}


   and update_by=#{updateBy}


   and update_time=#{updateTime}


   and version_num=#{versionNum}
